<script setup>
import { onBeforeMount, onMounted, onBeforeUnmount, ref } from 'vue'
import { browseSongs } from '@/api/routes/songs.js'
import { useUserStore } from '@/stores/user.js'
import { useLocalOptionsStore } from '@/stores/localOptions.js'
import SongTable from '@/components/SongTable.vue'

const userStore = useUserStore()
const localOptionsStore = useLocalOptionsStore()
const query = ref("")
const songs = ref([])
const loaderVisible = ref(false)
const greetingVisible = ref(true)
const siteDropdownActive = ref(false)
const dropdownArrow = ref(null)
const siteDropdownRef = ref(null)

const handleBrowse = async () => {
  if (!query.value.trim()) return

  greetingVisible.value = false

  songs.value = []
  loaderVisible.value = true

  const response = await browseSongs(query.value, localOptionsStore.browseOption)

  loaderVisible.value = false
  songs.value = response.songs
}

const changeSite = (siteName) => {
  localOptionsStore.setBrowseOption(siteName)
  toggleDropdown()
}

const toggleDropdown = () => {
  siteDropdownActive.value = !siteDropdownActive.value

  if (siteDropdownActive.value) dropdownArrow.value.style.transform = "rotate(180deg)"
  else dropdownArrow.value.style.transform = "rotate(0deg)"
}

const handleClick = (event) => {
  if (!siteDropdownRef.value.contains(event.target)) {
    if (siteDropdownActive.value) toggleDropdown()
  } else {
    toggleDropdown()
  }
}

onBeforeUnmount(() => {
  document.removeEventListener("click", handleClick)
})

onBeforeMount(async () => {
  await userStore.checkLogin()
  localOptionsStore.getLocalOptions()
})

onMounted(() => {
  document.addEventListener("click", handleClick)
})
</script>
